INSERT INTO `__logs` VALUES (97, 2, 'trungtuong', 'news-edit', 80, '127.0.0.1', '{\"id\":80,\"title\":\"Tr\\u00ean tay OPPO Reno3: Camera selfie \\u0111\\u00eam 44MP\",\"title_seo\":\"Tr\\u00ean tay OPPO Reno3: Camera selfie \\u0111\\u00eam 44MP, zoom 20x \\u0111\\u1ea7u ti\\u00ean ph\\u00e2n kh\\u00fac t\\u1ea7m trung\",\"sort_body\":\"<h5>Sau kho\\u1ea3ng g\\u1ea7n 2 th&aacute;ng ra m\\u1eaft th\\u1ecb tr\\u01b0\\u1eddng qu\\u1ed1c t\\u1ebf, nay OPPO Reno3 \\u0111&atilde; ch&iacute;nh th\\u1ee9c \\u0111\\u1ed5 b\\u1ed9 t\\u1ea1i Vi\\u1ec7t Nam. \\u0110&acirc;y l&agrave; chi\\u1ebfc smartphone k\\u1ebf nhi\\u1ec7m c\\u1ee7a Reno2 F tr\\u01b0\\u1edbc \\u0111&acirc;y, c&oacute; s\\u1ef1 c\\u1ea3i ti\\u1ebfn v\\u01b0\\u1ee3t tr\\u1ed9i v\\u1ec1 camera, \\u0111\\u1eb7c bi\\u1ec7t l&agrave; camera selfie.<\\/h5>\",\"body\":\"<p><a href=\\\"https:\\/\\/fptshop.com.vn\\/dien-thoai\\/oppo-reno-3\\\" target=\\\"_blank\\\">OPPO Reno3<\\/a>&nbsp;s\\u1ebd \\u0111\\u01b0\\u1ee3c ra m\\u1eaft ng&agrave;y mai 8\\/5 v\\u1edbi gi&aacute; b&aacute;n d\\u1ef1 ki\\u1ebfn 8.99 tri\\u1ec7u. \\u0110&acirc;y l&agrave; m\\u1ee9c gi&aacute; t\\u01b0\\u01a1ng \\u0111\\u01b0\\u01a1ng v\\u1edbi&nbsp;<a href=\\\"https:\\/\\/fptshop.com.vn\\/dien-thoai\\/oppo-reno-2f\\\" target=\\\"_blank\\\">OPPO Reno2 F<\\/a>&nbsp;t\\u1ea1i th\\u1eddi \\u0111i\\u1ec3m l&ecirc;n k\\u1ec7. R\\u1ea5t nhanh, FPT Shop \\u0111&atilde; c&oacute; s\\u1ea3n ph\\u1ea9m tr&ecirc;n tay \\u0111\\u1ec3 cho ng\\u01b0\\u1eddi d&ugrave;ng c&aacute;i nh&igrave;n c\\u1eadn c\\u1ea3nh v\\u1ec1 si&ecirc;u ph\\u1ea9m m\\u1edbi nh\\u1ea5t nh&agrave; OPPO.&nbsp;<\\/p>\\r\\n\\r\\n<p><img alt=\\\"OPPO Reno3\\\" src=\\\"https:\\/\\/cdn.fptshop.com.vn\\/uploads\\/images\\/tin-tuc\\/119911\\/Originals\\/HASP-Oppo-Reno-3-26.JPG\\\" \\/><\\/p>\\r\\n\\r\\n<p>\\u0110\\u1ea7u ti&ecirc;n l&agrave; ph\\u1ea7n thi\\u1ebft k\\u1ebf, s\\u1ef1 thay \\u0111\\u1ed5i n&agrave;y khi\\u1ebfn nhi\\u1ec1u ng\\u01b0\\u1eddi b\\u1ea5t ng\\u1edd khi d&ograve;