INSERT INTO `__logs` VALUES (159, 2, 'trungtuong', 'product-edit', 1810, '127.0.0.1', '{\"id\":1810,\"created\":1591076846,\"title\":\"New Dell XPS 15 9500 (2020) 10th 15.6\\\" Windown 10\",\"title_sub\":\"Core i7 10750H \\/ RAM 16GB \\/ SSD 256GB \\/ FHD+ \\/ GeForce\\u00ae GTX 1650Ti \\/ Win 10 Pro \\/ B\\u1ea3o h\\u00e0nh Dell t\\u1eadn nh\\u00e0\",\"alias\":\"new-dell-xps-15-9500-2020-10th-156-windown-10\",\"title_seo\":\"New Dell XPS 15 9500 (2020) 10th 15.6\\\" Windown 10\",\"safe_title\":null,\"status\":2,\"lang\":\"vi\",\"image_seo\":\"\",\"image\":\"new-dell-xps-15-9500-2020-10th-156-windown-10-1591076846.jpg\",\"price\":44900000,\"priceStrike\":45900000,\"code\":\"\",\"unit\":\"\",\"sort\":\"100\",\"sapo\":\"\",\"body\":\"<h3 style=\\\"text-align: justify;\\\">\\u0110&aacute;nh gi&aacute; chi ti\\u1ebft Samsung Galaxy A21s<\\/h3>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\"><b>Chi\\u1ebfc \\u0111i\\u1ec7n tho\\u1ea1i v\\u1edbi nh\\u1eefng \\u0111\\u1ed9t ph&aacute; \\u0111\\u1ea7y \\u1ea5n t\\u01b0\\u1ee3ng, Samsung Galaxy A21s s\\u1edf h\\u1eefu 4 camera sau \\u0111a ch\\u1ee9c n\\u0103ng, camera tr\\u01b0\\u1edbc n\\u1eb1m ngay trong t\\u1ea5m k&iacute;nh m&agrave;n h&igrave;nh, mang \\u0111\\u1ebfn tr\\u1ea3i nghi\\u1ec7m m&agrave;n h&igrave;nh l\\u1edbn h\\u1ea5p d\\u1eabn h\\u01a1n bao gi\\u1edd h\\u1ebft.<\\/b><\\/p>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\">&nbsp;<\\/p>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\"><b><img alt=\\\"Samsung Galaxy A21s\\\" data-original=\\\"https:\\/\\/images.fpt.shop\\/unsafe\\/fit-in\\/filters:quality(90)\\/fptshop.com.vn\\/Uploads\\/images\\/2015\\/Tin-Tuc\\/QuanLNH2\\/samsung-galaxy-a21s-1.jpg\\\" src=\\\"https:\\/\\/images.fpt.shop\\/unsafe\\/fit-in\\/filters:quality(90)\\/fptshop.com.vn\\/Uploads\\/images\\/2015\\/Tin-Tuc\\/QuanLNH2\\/samsung-galaxy-a21s-1.jpg\\\" \\/><\\/b><\\/p>\\r\\n\\r\\n<h3 style=\\\"text-align: justify;\\\"><b>M&agrave;n h&igrave;nh tr&agrave;n vi\\u1ec1n s\\u1ed1ng \\u0111\\u1ed9ng \\u0111\\u1ebfn kh&ocirc;ng ng\\u1edd<\\/b><\\/h3>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\">Tr\\u1ea3i nghi\\u1ec7m m&agrave;n h&igrave;nh c\\u1ee7a b\\u1ea1n \\u0111\\u01b0\\u1ee3c n&acirc;ng l&ecirc;n m\\u1ed9t t\\u1ea7m cao m\\u1edbi khi b&ecirc;n c\\u1ea1nh m&agrave;n h&igrave;nh tr&agrave;n vi\\u1ec1n, Samsung Galaxy A21s c&ograve;n \\u0111\\u1eb7t camera tr\\u01b0\\u1edbc n\\u1eb1m ngay trong t\\u1ea5m k&iacute;nh m&agrave;n h&igrave;nh, \\u0111\\u1ec3 t\\u1ed1i \\u01b0u kh&ocirc;ng gian hi\\u1ec3n th\\u1ecb. B\\u1ea1n s\\u1ebd \\u0111\\u01b0\\u1ee3c t\\u1eadn h\\u01b0\\u1edfng nh\\u1eefng h&igrave;nh \\u1ea3nh s\\u1ed1ng \\u0111\\u1ed9ng tr&ecirc;n m&agrave;n h&igrave;nh v&ocirc; c\\u1ef1c Infinity-O l\\u1edbn t\\u1edbi 6,5 inch. Th\\u1ecfa s\\u1ee9c xem video, ch\\u01a1i game, livestream v&agrave; l&agrave;m nh\\u1eefng \\u0111i\\u1ec1u b\\u1ea1n th&iacute;ch tr&ecirc;n m&agrave;n h&igrave;nh l\\u1edbn \\u0111\\u1ea7y \\u1ea5n t\\u01b0\\u1ee3ng.<\\/p>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\"><img alt=\\\"m\\u00e0n h\\u00ecnh Samsung Galaxy A21s\\\" data-original=\\\"https:\\/\\/images.fpt.shop\\/unsafe\\/fit-in\\/filters:quality(90)\\/fptshop.com.vn\\/Uploads\\/images\\/2015\\/Tin-Tuc\\/QuanLNH2\\/samsung-galaxy-a21s-2.jpg\\\" src=\\\"https:\\/\\/images.fpt.shop\\/unsafe\\/fit-in\\/filters:quality(90)\\/fptshop.com.vn\\/Uploads\\/images\\/2015\\/Tin-Tuc\\/QuanLNH2\\/samsung-galaxy-a21s-2.jpg\\\" \\/><\\/p>\\r\\n\\r\\n<h3 style=\\\"text-align: justify;\\\"><b>Ki\\u1ec3u d&aacute;ng th\\u1eddi trang, m&agrave;u s\\u1eafc t\\u01b0\\u01a1i tr\\u1ebb<\\/b><\\/h3>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\">Samsung Galaxy A21s mang tr&ecirc;n m&igrave;nh thi\\u1ebft k\\u1ebf tr\\u1ebb trung v\\u1edbi l\\u1edbp v\\u1ecf b&oacute;ng b\\u1ea9y, m\\u1eb7t l\\u01b0ng holographic m\\u1edbi l\\u1ea1, thu h&uacute;t m\\u1ecdi &aacute;nh nh&igrave;n. Thi\\u1ebft k\\u1ebf n&agrave;y \\u0111\\u01b0\\u1ee3c ho&agrave;n thi\\u1ec7n t\\u1ec9 m\\u1ec9 nh\\u1edd nh\\u1eefng \\u0111\\u01b0\\u1eddng bo cong tinh t\\u1ebf, ch\\u1eafc ch\\u1eafn v&agrave; li\\u1ec1n l\\u1ea1c. Nh\\u1edd m&agrave;n h&igrave;nh vi\\u1ec1n m\\u1ecfng t\\u1ed1i \\u01b0u n&ecirc;n Galaxy A21s kh&ocirc;ng qu&aacute; l\\u1edbn m&agrave; ng\\u01b0\\u1ee3c l\\u1ea1i, r\\u1ea5t v\\u1eeba v\\u1eb7n trong l&ograve;ng b\\u1ea1n tay b\\u1ea1n. S\\u1ebd c&oacute; \\u0111\\u1ea7y \\u0111\\u1ee7 nh\\u1eefng l\\u1ef1a ch\\u1ecdn m&agrave;u tr\\u1ebb trung nh\\u01b0 \\u0110en, Tr\\u1eafng hay Xanh ph&ugrave; h\\u1ee3p v\\u1edbi c&aacute; t&iacute;nh m\\u1ed7i ng\\u01b0\\u1eddi d&ugrave;ng.<\\/p>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\"><img alt=\\\"thi\\u1ebft k\\u1ebf Samsung Galaxy A21s\\\" data-original=\\\"https:\\/\\/images.fpt.shop\\/unsafe\\/fit-in\\/filters:quality(90)\\/fptshop.com.vn\\/Uploads\\/images\\/2015\\/Tin-Tuc\\/QuanLNH2\\/samsung-galaxy-a21s-3.jpg\\\" src=\\\"https:\\/\\/images.fpt.shop\\/unsafe\\/fit-in\\/filters:quality(90)\\/fptshop.com.vn\\/Uploads\\/images\\/2015\\/Tin-Tuc\\/QuanLNH2\\/samsung-galaxy-a21s-3.jpg\\\" \\/><\\/p>\\r\\n\\r\\n<h3 style=\\\"text-align: justify;\\\"><b>B\\u1ed9 t\\u1ee9 camera sau 48MP huy\\u1ec1n \\u1ea3o, \\u0111\\u1ea7y \\u0111\\u1ee7 t&iacute;nh n\\u0103ng ch\\u1ee5p \\u1ea3nh<\\/b><\\/h3>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\">N\\u1ebfu b\\u1ea1n l&agrave; ng\\u01b0\\u1eddi y&ecirc;u th&iacute;ch ch\\u1ee5p \\u1ea3nh, ch\\u1eafc h\\u1eb3n b\\u1ea1n s\\u1ebd r\\u1ea5t h\\u1ee9ng th&uacute; v\\u1edbi Samsung Galaxy A21s. Tha h\\u1ed3 s&aacute;ng t\\u1ea1o, tho\\u1ea3i m&aacute;i bi\\u1ebfn h&oacute;a v\\u1edbi 4 camera sau tr&ecirc;n chi\\u1ebfc \\u0111i\\u1ec7n tho\\u1ea1i \\u0111\\u1ea7y th&uacute; v\\u1ecb n&agrave;y. B&ecirc;n c\\u1ea1nh camera ch&iacute;nh \\u0111\\u1ed9 ph&acirc;n gi\\u1ea3i l&ecirc;n t\\u1edbi 48MP, Galaxy A21s c&ograve;n s\\u1edf h\\u1eefu camera g&oacute;c si&ecirc;u r\\u1ed9ng 8MP, camera h\\u1ed7 tr\\u1ee3 \\u0111o \\u0111\\u1ed9 s&acirc;u 2MP v&agrave; camera macro 2MP. D&ugrave; l&agrave; ch\\u1ee5p ng&agrave;y hay ch\\u1ee5p \\u0111&ecirc;m, ch\\u1ee5p \\u1edf xa hay kho\\u1ea3ng c&aacute;ch si&ecirc;u g\\u1ea7n, Galaxy A21s v\\u1eabn \\u0111\\u1ee7 kh\\u1ea3 n\\u0103ng \\u0111\\u1ec3 ghi l\\u1ea1i c&aacute;c kho\\u1ea3nh kh\\u1eafc m\\u1ed9t c&aacute;ch r&otilde; r&agrave;ng, chi ti\\u1ebft v&agrave; ch\\u1ea5t l\\u01b0\\u1ee3ng cao.<\\/p>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\"><img alt=\\\"camera Samsung Galaxy A21s\\\" data-original=\\\"https:\\/\\/images.fpt.shop\\/unsafe\\/fit-in\\/filters:quality(90)\\/fptshop.com.vn\\/Uploads\\/images\\/2015\\/Tin-Tuc\\/QuanLNH2\\/samsung-galaxy-a21s-4.jpg\\\" src=\\\"https:\\/\\/images.fpt.shop\\/unsafe\\/fit-in\\/filters:quality(90)\\/fptshop.com.vn\\/Uploads\\/images\\/2015\\/Tin-Tuc\\/QuanLNH2\\/samsung-galaxy-a21s-4.jpg\\\" \\/><\\/p>\\r\\n\\r\\n<h3 style=\\\"text-align: justify;\\\"><b>Mang c\\u1ea3nh \\u0111\\u1eb9p r\\u1ed9ng l\\u1edbn v&agrave;o khung h&igrave;nh<\\/b><\\/h3>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\">S\\u1ebd c&oacute; nh\\u1eefng l&uacute;c b\\u1ea1n g\\u1eb7p nh\\u1eefng c\\u1ea3nh \\u0111\\u1eb9p t\\u1eeb thi&ecirc;n nhi&ecirc;n, hay m\\u1ed9t c&ocirc;ng tr&igrave;nh ki\\u1ebfn tr&uacute;c \\u0111\\u1ed9c \\u0111&aacute;o, nh\\u01b0ng \\u1ed1ng k&iacute;nh camera l\\u1ea1i kh&ocirc;ng \\u0111\\u1ee7 r\\u1ed9ng \\u0111\\u1ec3 ch\\u1ee5p to&agrave;n c\\u1ea3nh. V\\u1edbi camera g&oacute;c si&ecirc;u r\\u1ed9ng l&ecirc;n t\\u1edbi 123 \\u0111\\u1ed9 c\\u1ee7a Samsung Galaxy A21s, m\\u1ecdi th\\u1ee9 th\\u1eadt d\\u1ec5 d&agrave;ng \\u0111\\u1ec3 b\\u1ea1n l\\u01b0u l\\u1ea1i c\\u1ea3nh \\u0111\\u1eb9p ch\\u1ec9 trong m\\u1ed9t khung h&igrave;nh.<\\/p>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\"><img alt=\\\"g\\u00f3c si\\u00eau r\\u1ed9ng Samsung Galaxy A21s\\\" data-original=\\\"https:\\/\\/images.fpt.shop\\/unsafe\\/fit-in\\/filters:quality(90)\\/fptshop.com.vn\\/Uploads\\/images\\/2015\\/Tin-Tuc\\/QuanLNH2\\/samsung-galaxy-a21s-5.jpg\\\" src=\\\"https:\\/\\/images.fpt.shop\\/unsafe\\/fit-in\\/filters:quality(90)\\/fptshop.com.vn\\/Uploads\\/images\\/2015\\/Tin-Tuc\\/QuanLNH2\\/samsung-galaxy-a21s-5.jpg\\\" \\/><\\/p>\\r\\n\\r\\n<h3 style=\\\"text-align: justify;\\\"><b>B\\u1eaft tr\\u1ecdn v\\u1ebb \\u0111\\u1eb9p c\\u1eadn c\\u1ea3nh<\\/b><\\/h3>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\">C&ugrave;ng Galaxy A21s kh&aacute;m ph&aacute; nh\\u1eefng chi ti\\u1ebft th&uacute; v\\u1ecb \\u1edf kho\\u1ea3ng c&aacute;ch si&ecirc;u g\\u1ea7n. S\\u1eed d\\u1ee5ng camera Macro gi&uacute;p b\\u1ea1n c&oacute; th\\u1ec3 l\\u1ea5y n&eacute;t c\\u1eadn c\\u1ea3nh v\\u1edbi kho\\u1ea3ng c&aacute;ch ch\\u1ec9 t\\u1eeb 3 &ndash; 5 cm. Nh\\u1eefng b&ocirc;ng hoa, chi\\u1ebfc l&aacute;, hay th\\u1eadm ch&iacute; chi\\u1ebfc b&aacute;nh b\\u1ea1n \\u0103n c\\u0169ng tr\\u1edf n&ecirc;n th&uacute; v\\u1ecb h\\u01a1n nhi\\u1ec1u qua \\u1ed1ng k&iacute;nh camera Macro c\\u1ee7a Samsung Galaxy A21s.<\\/p>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\"><img alt=\\\"camera macro Samsung Galaxy A21s\\\" data-original=\\\"https:\\/\\/images.fpt.shop\\/unsafe\\/fit-in\\/filters:quality(90)\\/fptshop.com.vn\\/Uploads\\/images\\/2015\\/Tin-Tuc\\/QuanLNH2\\/samsung-galaxy-a21s-6.jpg\\\" src=\\\"https:\\/\\/images.fpt.shop\\/unsafe\\/fit-in\\/filters:quality(90)\\/fptshop.com.vn\\/Uploads\\/images\\/2015\\/Tin-Tuc\\/QuanLNH2\\/samsung-galaxy-a21s-6.jpg\\\" \\/><\\/p>\\r\\n\\r\\n<h3 style=\\\"text-align: justify;\\\"><b>Hi\\u1ec7u \\u1ee9ng x&oacute;a ph&ocirc;ng chuy&ecirc;n nghi\\u1ec7p, t&ocirc;n vinh v\\u1ebb \\u0111\\u1eb9p c\\u1ee7a b\\u1ea1n<\\/b><\\/h3>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\">S\\u1ef1 k\\u1ebft h\\u1ee3p c\\u1ee7a camera ch&iacute;nh 48MP v&agrave; camera x&oacute;a ph&ocirc;ng 2MP gi&uacute;p Samsung Galaxy A21s c&oacute; th\\u1ec3 l&agrave;m m\\u1edd h\\u1eadu c\\u1ea3nh, \\u0111i\\u1ec1u ch\\u1ec9nh \\u0111\\u1ed9 m\\u1edd d&ugrave; l&agrave; tr\\u01b0\\u1edbc hay sau khi ch\\u1ee5p. K\\u1ebft qu\\u1ea3 l&agrave; b\\u1ea1n s\\u1ebd c&oacute; nh\\u1eefng b\\u1ee9c \\u1ea3nh x&oacute;a ph&ocirc;ng chuy&ecirc;n nghi\\u1ec7p, \\u0111\\u1eb9p ho&agrave;n h\\u1ea3o v\\u1edbi \\u0111\\u1ed9 s\\u1eafc n&eacute;t cao v&agrave; hi\\u1ec7u \\u1ee9ng t&aacute;ch ph&ocirc;ng n\\u1ec1n ch&iacute;nh x&aacute;c.<\\/p>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\"><img alt=\\\"x\\u00f3a ph\\u00f4ng Samsung Galaxy A21s\\\" data-original=\\\"https:\\/\\/images.fpt.shop\\/unsafe\\/fit-in\\/filters:quality(90)\\/fptshop.com.vn\\/Uploads\\/images\\/2015\\/Tin-Tuc\\/QuanLNH2\\/samsung-galaxy-a21s-7.jpg\\\" src=\\\"https:\\/\\/images.fpt.shop\\/unsafe\\/fit-in\\/filters:quality(90)\\/fptshop.com.vn\\/Uploads\\/images\\/2015\\/Tin-Tuc\\/QuanLNH2\\/samsung-galaxy-a21s-7.jpg\\\" \\/><\\/p>\\r\\n\\r\\n<h3 style=\\\"text-align: justify;\\\"><b>T\\u1ecfa s&aacute;ng v\\u1edbi camera selfie 13MP<\\/b><\\/h3>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\">Camera tr\\u01b0\\u1edbc c\\u1ee7a Samsung Galaxy A21s c\\u0169ng c&oacute; ch\\u1ea5t l\\u01b0\\u1ee3ng kh&ocirc;ng h\\u1ec1 k&eacute;m c\\u1ea1nh camera sau. \\u0110\\u1ed9 ph&acirc;n gi\\u1ea3i 13MP c&ugrave;ng nh\\u1eefng thu\\u1eadt to&aacute;n th&ocirc;ng minh mang \\u0111\\u1ebfn nh\\u1eefng b\\u1ee9c \\u1ea3nh &ldquo;t\\u1ef1 s\\u01b0\\u1edbng&rdquo; \\u0111\\u1ea7y \\u1ea5n t\\u01b0\\u1ee3ng. Ngo&agrave;i vi\\u1ec7c kh\\u1eafc h\\u1ecda khu&ocirc;n m\\u1eb7t c\\u1ee7a b\\u1ea1n \\u0111\\u1ea7y chi ti\\u1ebft, Galaxy A21s c&ograve;n kh&eacute;o l&eacute;o ch\\u1ec9nh s\\u1eeda tinh t\\u1ebf \\u0111\\u1ec3 b\\u1ea1n c&oacute; th\\u1ec3 t\\u1ef1 tin \\u0111\\u1eb9p t\\u1ecfa s&aacute;ng.<\\/p>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\"><img alt=\\\"camera selfie Samsung Galaxy A21s\\\" data-original=\\\"https:\\/\\/images.fpt.shop\\/unsafe\\/fit-in\\/filters:quality(90)\\/fptshop.com.vn\\/Uploads\\/images\\/2015\\/Tin-Tuc\\/QuanLNH2\\/samsung-galaxy-a21s-8.jpg\\\" src=\\\"https:\\/\\/images.fpt.shop\\/unsafe\\/fit-in\\/filters:quality(90)\\/fptshop.com.vn\\/Uploads\\/images\\/2015\\/Tin-Tuc\\/QuanLNH2\\/samsung-galaxy-a21s-8.jpg\\\" \\/><\\/p>\\r\\n\\r\\n<h3 style=\\\"text-align: justify;\\\"><b>Si&ecirc;u pin dung l\\u01b0\\u1ee3ng 5000 mAh<\\/b><\\/h3>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\">Samsung Galaxy A21s c&oacute; th\\u1eddi l\\u01b0\\u1ee3ng pin \\u0111&aacute;ng kinh ng\\u1ea1c nh\\u1edd vi&ecirc;n pin dung l\\u01b0\\u1ee3ng c\\u1ef1c &ldquo;kh\\u1ee7ng&rdquo; 5000 mAh. B\\u1ea1n c&oacute; th\\u1ec3 tho\\u1ea3i m&aacute;i ch\\u01a1i game, xem phim, v&agrave;o m\\u1ea1ng x&atilde; h\\u1ed9i v&agrave; m\\u1ecdi \\u1ee9ng d\\u1ee5ng b\\u1ea1n mu\\u1ed1n trong c\\u1ea3 ng&agrave;y m&agrave; kh&ocirc;ng lo h\\u1ebft pin. Ngay c\\u1ea3 khi pin s\\u1eafp h\\u1ebft c\\u0169ng kh&ocirc;ng c\\u1ea7n ph\\u1ea3i lo l\\u1eafng, v&igrave; v\\u1edbi c&ocirc;ng ngh\\u1ec7 s\\u1ea1c nhanh si&ecirc;u t\\u1ed1c, Galaxy A21s s\\u1ebd nhanh ch&oacute;ng &ldquo;h\\u1ed3i ph\\u1ee5c n\\u0103ng l\\u01b0\\u1ee3ng&rdquo;.<\\/p>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\"><img alt=\\\"pin dung l\\u01b0\\u1ee3ng cao Samsung Galaxy A21s\\\" data-original=\\\"https:\\/\\/images.fpt.shop\\/unsafe\\/fit-in\\/filters:quality(90)\\/fptshop.com.vn\\/Uploads\\/images\\/2015\\/Tin-Tuc\\/QuanLNH2\\/samsung-galaxy-a21s-9.jpg\\\" src=\\\"https:\\/\\/images.fpt.shop\\/unsafe\\/fit-in\\/filters:quality(90)\\/fptshop.com.vn\\/Uploads\\/images\\/2015\\/Tin-Tuc\\/QuanLNH2\\/samsung-galaxy-a21s-9.jpg\\\" \\/><\\/p>\\r\\n\\r\\n<h3 style=\\\"text-align: justify;\\\"><b>C\\u1ea5u h&igrave;nh m\\u1ea1nh m\\u1ebd<\\/b><\\/h3>\\r\\n\\r\\n<p style=\\\"text-align: justify;\\\">B\\u1ed9 vi x\\u1eed l&yacute; 8 nh&acirc;n Exynos 850 do ch&iacute;nh Samsung s\\u1ea3n xu\\u1ea5t s\\u1ebd gi&uacute;p cho Galaxy A21s c&oacute; \\u0111\\u01b0\\u1ee3c hi\\u1ec7u n\\u0103ng \\u0111&uacute;ng nh\\u01b0 k\\u1ef3 v\\u1ecdng. T\\u1ed1c \\u0111\\u1ed9 x\\u1eed l&yacute; nhanh, c&aacute;c thao t&aa