INSERT INTO `__logs` VALUES (2, 2, 'trungtuong', 'news-edit', 4, '123.30.170.250', '{\"id\":4,\"title\":\"H\\u00e3y b\\u1ecf t\\u00fai nh\\u1eefng m\\u1eb9o ch\\u1ebf \\u0111\\u1ed9 n\\u1ec1n t\\u1ed1i t\\u1ed1t nh\\u1ea5t tr\\u00ean iOS 13\",\"title_seo\":\"H\\u00e3y b\\u1ecf t\\u00fai nh\\u1eefng m\\u1eb9o ch\\u1ebf \\u0111\\u1ed9 n\\u1ec1n t\\u1ed1i t\\u1ed1t nh\\u1ea5t tr\\u00ean iOS 13\",\"sort_body\":\"<p>H\\u1ec7 \\u0111i\\u1ec1u h&agrave;nh iOS 13 \\u0111&atilde; ch&iacute;nh th\\u1ee9c \\u0111\\u1ebfn v\\u1edbi ng\\u01b0\\u1eddi d&ugrave;ng v&agrave; th\\u1ef1c s\\u1ef1 mang l\\u1ea1i tr\\u1ea3i nghi\\u1ec7m ph\\u1ea7n nh&igrave;n kh&aacute; &quot;\\u0111&atilde;&quot;.<\\/p>\",\"body\":\"<p style=\\\"text-align:justify\\\">Trong \\u0111&oacute; m\\u1ed9t t&iacute;nh n\\u0103ng quan tr\\u1ecdng tr&ecirc;n h\\u1ec7 \\u0111i\\u1ec1u h&agrave;nh&nbsp;iOS 13&nbsp;l&agrave; ch\\u1ebf \\u0111\\u1ed9 n\\u1ec1n t\\u1ed1i tr&ecirc;n to&agrave;n h\\u1ec7 th\\u1ed1ng kh&aacute; \\u0111\\u1eb9p m\\u1eaft v&agrave; c\\u1ea3i thi\\u1ec7n v\\u1ec1 th\\u1eddi l\\u01b0\\u1ee3ng pin. D\\u01b0\\u1edbi b&agrave;i vi\\u1ebft n&agrave;y,&nbsp;<strong>FPT Shop<\\/strong>&nbsp;s\\u1ebd chia s\\u1ebb cho c&aacute;c b\\u1ea1n nh\\u1eefng&nbsp;th\\u1ee7 thu\\u1eadt&nbsp;v\\u1ec1 ch\\u1ebf \\u0111\\u1ed9 n\\u1ec1n t\\u1ed1i m&agrave; b\\u1ea1n n&ecirc;n b\\u1ecf t&uacute;i cho m&igrave;nh.<\\/p>\\r\\n\\r\\n<p style=\\\"text-align:center\\\"><img alt=\\\"ios-13-dark-mode-1575014397.jpg\\\" src=\\\"https:\\/\\/lapvip.vn\\/upload\\/file\\/thumb_800x0\\/ios-13-dark-mode-1575014397.jpg\\\" \\/><\\/p>\\r\\n\\r\\n<p style=\\\"text-align:justify\\\">Nh\\u1eefng thi\\u1ebft b\\u1ecb nh\\u01b0 iPhone SE v\\u1eabn l&ecirc;n \\u0111\\u1eddi \\u0111\\u01b0\\u1ee3c h\\u1ec7 \\u0111i\\u1ec1u h&agrave;nh iOS 13 n\\u1ebfu ch\\u01b0a c\\u1eadp nh\\u1eadt b\\u1ea1n h&atilde;y v&agrave;o ph\\u1ea7n c&agrave;i \\u0111\\u1eb7t v&agrave; t\\u1ea3i xu\\u1ed1ng b\\u1ea3n c\\u1eadp nh\\u1eadt ph\\u1ea7n m\\u1ec1m m\\u1edbi nh\\u1ea5t nh&eacute;!<\\/p>\\r\\n\\r\\n<h3 style=\\\"text-align:justify\\\"><strong>1. C&aacute;ch k&iacute;ch ho\\u1ea1t ch\\u1ebf \\u0111\\u1ed9 n\\u1ec1n t\\u1ed1i trong iOS 13<\\/strong><\\/h3>\\r\\n\\r\\n<p style=\\\"text-align:justify\\\">B\\u1ea1n ch\\u1ec9 c\\u1ea7n m\\u1edf&nbsp;<strong><em>C&agrave;i \\u0111\\u1eb7t<\\/em><\\/strong>&nbsp;v&agrave; nh\\u1ea7n v&agrave;o&nbsp;<strong><em>Hi\\u1ec3n th\\u1ecb &amp; \\u0110\\u1ed9 s&aacute;ng<\\/em><\\/strong>.<\\/p>\\r\\n\\r\\n<p style=\\\"text-align:center\\\"><img alt=\\\"iphone-8-1575014439.jpg\\\" src=\\\"https:\\/\\/lapvip.vn\\/upload\\/file\\/thumb_800x0\\/iphone-8-1575014439.jpg\\\" \\/><\\/p>\\r\\n\\r\\n<p style=\\\"text-align:justify\\\">B\\u1ea1n s\\u1ebd th\\u1ea5y s\\u1ef1 xu\\u1ea5t hi\\u1ec7n c\\u1ee7a l\\u1ef1a ch\\u1ecdn n\\u1ec1n t\\u1ed1i. Ch\\u1ebf \\u0111\\u1ed9 t\\u1ed1i trong iOS 13 c&oacute; giao di\\u1ec7n t\\u1ed1i nh\\u1ea1t h\\u01a1i n&acirc;u, kh&ocirc;ng qu&aacute; g\\u1eaft nh\\u01b0 ch\\u1ebf \\u0111\\u1ed9 n\\u1ec1n t\\u1ed1i c\\u1ee7a Facebook v&agrave; nh\\u1eadn \\u0111\\u01b0\\u1ee3c nhi\\u1ec1u l\\u1eddi khen.<\\/p>\\r\\n\\r\\n<p style=\\\"text-align:center\\\"><img alt=\\\"iphone-7-1575014455.jpg\\\" src=\\\"https:\\/\\/lapvip.vn\\/upload\\/file\\/thumb_800x0\\/iphone-7-1575014455.jpg\\\" \\/><\\/p>\\r\\n\\r\\n<p style=\\\"text-align:justify\\\">N\\u1ec1n \\u0111en nh\\u01b0ng c&aacute;c ph&ocirc;ng ch\\u1eef c&oacute; m&agrave;u tr\\u1eafng, t\\u1ea5t c\\u1ea3 c&aacute;c t&ugrave;y ch\\u1ecdn c&oacute; th\\u1ec3 nh\\u1ea5n ho\\u1eb7c ch\\u1ecdn v\\u0103n b\\u1ea3n c&oacute; m&agrave;u xanh c\\u1ed5 \\u0111i\\u1ec3n.<\\/p>\\r\\n\\r\\n<h3 style=\\\"text-align:justify\\\"><strong>2. B\\u1eadt ch\\u1ebf \\u0111\\u1ed9 n\\u1ec1n t\\u1ed1i trong trung t&acirc;m \\u0111i\\u1ec1u khi\\u1ec3n<\\/strong><\\/h3>\\r\\n\\r\\n<p style=\\\"text-align:justify\\\">Ng\\u01b0\\u1eddi d&ugrave;ng v\\u1eabn c&oacute; th\\u1ec3 b\\u1eadt nhanh ch\\u1ebf \\u0111\\u1ed9 n\\u1ec1