INSERT INTO `old_collection` VALUES (5, 'CHƯƠNG TRÌNH THU CŨ ĐỔI MỚI IPHONE 2019', 'CHƯƠNG TRÌNH THU CŨ ĐỔI MỚI IPHONE 2019', '<h1>Thu cũ đổi mới với ch&iacute;nh s&aacute;ch&nbsp;<strong>5 KH&Ocirc;NG</strong>&nbsp;tại Lapvip.vn</h1>\r\n\r\n<p>Lapvip.vn&nbsp;tự h&agrave;o l&agrave; sự lựa chọn h&agrave;ng đầu của nhiều kh&aacute;ch h&agrave;ng v&agrave; hơn 40 sao Việt hạng A đổi iPhone cũ lấy iPhone XS Max, đổi điện thoại cũ lấy Galaxy S10, S10 Plus v&agrave; c&aacute;c d&ograve;ng m&aacute;y kh&aacute;c với ch&iacute;nh s&aacute;ch&nbsp;<strong>5 KH&Ocirc;NG</strong>&nbsp;cực k&igrave; đặc biệt.</p>\r\n\r\n<p>Lapvip.vn&nbsp;chấp nhận thu đổi cả ngay cả trong trường hợp m&aacute;y của kh&aacute;ch h&agrave;ng&nbsp;<strong>kh&ocirc;ng hoạt động được</strong>, kh&ocirc;ng c&oacute; phụ kiện... v&agrave; kh&ocirc;ng quan trọng xuất xứ.</p>\r\n\r\n<p>Tất cả những g&igrave; kh&aacute;ch h&agrave;ng cần l&agrave;m, l&agrave; mang m&aacute;y cũ đến,chọn một chiếc m&aacute;y m&igrave;nh muốn l&ecirc;n đời tại Lapvip.vn rồi mang m&aacute;y mới về nh&agrave;. Đơn giản, nhanh gọn, chỉ trong 10 ph&uacute;t l&agrave; c&oacute; m&aacute;y mới sử dụng ngay m&agrave;&nbsp;<strong>KH&Ocirc;NG CẦN B&Ugrave; TIỀN.</strong></p>', '<h5>1. Thời gian &aacute;p dụng:</h5>\r\n\r\n<p>Từ ng&agrave;y 11/09/2019</p>\r\n\r\n<h5>2. Đối tượng tham gia:</h5>\r\n\r\n<p>Kh&aacute;ch h&agrave;ng hiện l&agrave; chủ sở hữu điện thoại iPhone cũ, x&aacute;ch tay hoặc ch&iacute;nh h&atilde;ng, thoả c&aacute;c y&ecirc;u cầu về t&igrave;nh trạng điện thoại như b&ecirc;n dưới.</p>\r\n\r\n<h5>3. C&aacute;c ưu đ&atilde;i đi k&egrave;m:</h5>\r\n\r\n<p>Được &aacute;p dụng chung với c&aacute;c chương tr&igrave;nh trả g&oacute;p tại LAPVIP :<strong>GI&Aacute; B&Ugrave; CH&Ecirc;NH LỆCH (SỐ TIỀN KH&Aacute;CH TRẢ) = GI&Aacute; NI&Ecirc;M YẾT H&Atilde;NG - KHUYẾN MẠI QU&Agrave; HOẶC PMH LAPVIP&nbsp;- GI&Aacute; NHẬP M&Aacute;Y CŨ - TRỢ GI&Aacute; (nếu c&oacute;)</strong></p>\r\n\r\n<h5>4. C&aacute;c model thu mua:</h5>\r\n\r\n<p>C&aacute;c model nằm trong danh s&aacute;ch b&ecirc;n dưới c&aacute;c model kh&ocirc;ng nằm trong danh s&aacute;ch, qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ cửa h&agrave;ng để được hỗ trợ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"1px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td><strong>Thu loại 1</strong></td>\r\n			<td><strong>Thu loại 2</strong></td>\r\n			<td><strong>Thu loại 3</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Nguồn v&agrave; bảo mật</strong></td>\r\n			<td colspan=\"3\">\r\n			<p>Nguồn c&ograve;n hoạt động được, đ&atilde; tho&aacute;t khỏi c&aacute;c chức năng bảo mật</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>M&agrave;n h&igrave;nh</strong></td>\r\n			<td colspan=\"3\">\r\n			<p>M&agrave;n h&igrave;nh đẹp.<br />\r\n			Kh&ocirc;ng bị c&aacute;c lỗi &aacute;m ảnh, điểm chết, tr&oacute;c thuỷ, &acirc;m ảnh, ...</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Chức năng</strong></td>\r\n			<td colspan=\"2\">\r\n			<p>C&ograve;n đầy đủ chức năng.</p>\r\n			</td>\r\n			<td>\r\n			<p>Thiếu 1 số chức năng kh&ocirc;ng ảnh hưởng lớn tới m&aacute;y (v&iacute; dụ camera, rung,...</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>H&igrave;nh thức</strong></td>\r\n			<td colspan=\"2\">\r\n			<p>H&igrave;nh thức m&aacute;y đẹp, như mới 99% H&igrave;nh thức m&aacute;y kh&ocirc;ng đẹp (90-95%), xước, cấn</p>\r\n			</td>\r\n			<td>\r\n			<p>H&igrave;nh thức m&aacute;y bị vỡ k&iacute;nh, cấn nặng</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h5>6. C&aacute;c loại kh&ocirc;ng thu mua</h5>\r\n\r\n<p>- M&aacute;y bị lỗi khởi động, kh&ocirc;ng log out được khỏi tất cả c&aacute;c t&iacute;nh năng bảo mật m&aacute;y v&agrave; kho&aacute; m&aacute;y<br />\r\n- M&aacute;y kh&ocirc;ng l&ecirc;n nguồn, hỏng cảm ứng m&agrave;n h&igrave;nh, bể vỡ LCD<br />\r\n- M&aacute;y iPhone đ&atilde; bị Jailbreak, Lock, m&aacute;y bị hỏng hoặc mất n&uacute;t Home</p>', '', 1569921708, -28800, 2, 'vi', 0, 'chuong-trinh-thu-cu-doi-moi-iphone-2019', NULL, NULL, 0, 0, NULL, NULL, 3, NULL, NULL);
INSERT INTO `old_collection` VALUES (6, 'Thu mua Laptop cũ giá tốt nhất tại Hà Nội', 'Thu mua Laptop cũ giá tốt nhất tại Hà Nội', '<p><span style=\"font-size:28px;\"><span style=\"font-family:Times New Roman,Times,serif;\">Chương tr&igrave;nh thu mua laptop cũ gi&aacute; tối nhất tại H&agrave; Nội chỉ c&oacute; ở Lapvip.vn</span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:18px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">Nguy&ecirc;n tắc thu mua laptop cũ của ch&uacute;ng t&ocirc;i</span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:18px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">&gt;&gt;Thuận mua vừa b&aacute;n &ndash; đ&ocirc;i b&ecirc;n c&ugrave;ng c&oacute; lợi</span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:18px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">&gt;&gt;Gi&aacute; đưa ra lu&ocirc;n cao hơn c&aacute;c cửa hang m&aacute;y t&iacute;nh kh&aacute;c tại H&agrave; Nội </span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:18px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">&gt;&gt;Đội ngũ nh&acirc;n vi&ecirc;n nhiều kinh nghiệm v&agrave; nhiệt t&igrave;nh với kh&aacute;ch hang</span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:18px;\"><span style=\"font-family:Times New Roman,Times,serif;\"><span style=\"line-height:107%\"><span style=\"line-height:107%\">&gt;&gt;Kh&ocirc;ng k&igrave; k&egrave;o &eacute;p gi&aacute;</span></span></span></span></p>\r\n\r\n<p style=\"margin-bottom:11px\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"line-height:107%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">H&atilde;y li&ecirc;n hệ với hotline 0911515888 để được hỗ trợ v&agrave; tư vấn.</span></span></span></span></span></span></p>', '<p style=\"margin-bottom: 11px; text-align: center;\"><img alt=\"banner-mua-laptop3-1575009941.png\" src=\"https://lapvip.vn/upload/file/thumb_800x0/banner-mua-laptop3-1575009941.png\" /></p>\r\n\r\n<p style=\"margin-bottom: 11px;\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"line-height:107%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Bạn đang sử dụng một chiếc laptop cũ v&agrave; hoạt động k&eacute;m, bạn đang muốn n&acirc;ng cấp l&ecirc;n đời laptop cao hơn, nhưng kh&ocirc;ng biết <b>b&aacute;n <a href=\"https://lapvip.vn/kho-laptop-cu-p84\">laptop cũ</a> ở đ&acirc;u</b> v&agrave; <b>mua <a href=\"https://lapvip.vn/laptop-p82\">laptop mới </a>chỗ n&agrave;o tại H&agrave; Nội</b>? Vậy h&atilde;y đọc b&agrave;i viết dưới đ&acirc;y để biết th&ecirc;m về chương tr&igrave;nh <b>thu mua laptop cũ gi&aacute; cao tại H&agrave; Nội</b> nh&eacute;! </span></span></span></span></span></span></p>\r\n\r\n<h2 style=\"margin-bottom: 11px;\"><strong><span style=\"font-size:22px;\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"line-height:107%\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">V&igrave; sao b&aacute;n laptop cũ l&agrave; điều được khuyến kh&iacute;ch?</span></span></span></span></span></strong></h2>\r\n\r\n<p style=\"margin-bottom: 11px;\"><span style=\"font-size:11pt\"><span style=\"line-height:107%\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"line-height:107%\"><span style=\"font-family:&quot;Times New Roman&